mapper.select:
SELECT BID, BNAME, BOPEN_STATUS, BINTRO, CNAME, PID, EDIT_NAME FROM( SELECT * FROM ( SELECT B.BID, B.BNAME, B.BOPEN_STATUS, B.BINTRO, C.CNAME, P.PID, P.EDIT_NAME, P.FTYPE, P.FILE_SRC, ROWNUM FROM BAND B JOIN CATEGORY C ON(B.CID = C.CID) JOIN PROFILE P ON(B.BID = P.BID) WHERE B.BOPEN_STATUS != 'PRV' AND B.BSTATUS = 'Y' ORDER BY B.BID DESC )
OR UPPER(BNAME) LIKE '%'|| UPPER(#{item}) ||'%' OR UPPER(BINTRO) LIKE '%'|| UPPER(#{item}) ||'%'
)

SELECT ROWNUM, BID, BNAME, BOPEN_STATUS, BINTRO, CNAME, PID, EDIT_NAME FROM( SELECT ROWNUM AS RNUM, BID, BNAME, BOPEN_STATUS, BINTRO, CNAME, PID, EDIT_NAME FROM ( SELECT B.BID, B.BNAME, B.BOPEN_STATUS, B.BINTRO, C.CNAME, P.PID, P.EDIT_NAME, P.FTYPE, P.FILE_SRC, ROWNUM FROM BAND B JOIN CATEGORY C ON(B.CID = C.CID) JOIN PROFILE P ON(B.BID = P.BID) WHERE B.BOPEN_STATUS != 'PRV' AND B.BSTATUS = 'Y' ORDER BY B.BID DESC )
OR UPPER(BNAME) LIKE '%'|| UPPER(#{item}) ||'%' OR UPPER(BINTRO) LIKE '%'|| UPPER(#{item}) ||'%'
) WHERE RNUM <= #{endPage} AND RNUM >= #{startPage}

SELECT BID, BNAME, BOPEN_STATUS, BINTRO, MID, MNAME, CNAME, BOARDID, BCONTENT, BDATE, PID, EDIT_NAME, FTYPE, FILE_SRC, FID, A_EDIT_NAME, A_File_SRC, FLEVEL FROM(SELECT ROWNUM AS RNUM, BID, BNAME, BOPEN_STATUS, BINTRO, MID, MNAME, CNAME, BOARDID, BCONTENT, BDATE, PID, EDIT_NAME, FTYPE, FILE_SRC, FID, A_EDIT_NAME, A_File_SRC, FLEVEL FROM (SELECT B.BID, B.BNAME, B.BOPEN_STATUS, B.BINTRO, M.MID, M.MNAME, C.CNAME, BOD.BOARDID, BOD.BCONTENT, BOD.BDATE, P.PID, P.EDIT_NAME, P.FTYPE, P.FILE_SRC, AT.FID, AT.EDIT_NAME as A_EDIT_NAME, AT.FILE_SRC as A_File_SRC, AT.FLEVEL, ROWNUM FROM BAND B JOIN BOARDS BOD ON(BOD.BID = B.BID) JOIN MEMBER M ON(BOD.MID = M.MID) JOIN CATEGORY C ON(B.CID = C.CID) JOIN PROFILE P ON(B.BID = P.BID) RIGHT JOIN ATTFILE AT ON(AT.BOARDID = BOD.BOARDID) WHERE B.BOPEN_STATUS != 'PRV' AND B.BSTATUS = 'Y' ORDER BY BDATE DESC )
OR UPPER(BCONTENT) LIKE '%'|| UPPER(#{item}) ||'%'
)
