mapper.select:
SELECT BID, BNAME, BOPEN_STATUS, BINTRO, MID, MNAME, CNAME, PID, EDIT_NAME FROM( SELECT * FROM ( SELECT B.BID, B.BNAME, B.BOPEN_STATUS, B.BINTRO, M.MID, M.MNAME, C.CNAME, P.PID, P.EDIT_NAME, P.FTYPE, P.FILE_SRC, ROWNUM FROM BAND B JOIN MEMBER_BAND MB ON(B.BID = MB.BID) JOIN MEMBER M ON(MB.MID = M.MID) JOIN CATEGORY C ON(B.CID = C.CID) JOIN PROFILE P ON(B.BID = P.BID) WHERE B.BOPEN_STATUS != 'PRV' AND B.BSTATUS = 'Y' ORDER BY B.BID DESC )
OR UPPER(BNAME) LIKE '%'|| UPPER(#{item}) ||'%' OR UPPER(BINTRO) LIKE '%'|| UPPER(#{item}) ||'%'
) WHERE ROWNUM <= 5

SELECT ROWNUM, BID, BNAME, BOPEN_STATUS, BINTRO, MID, MNAME, CNAME, PID, EDIT_NAME FROM( SELECT ROWNUM AS RNUM, BID, BNAME, BOPEN_STATUS, BINTRO, MID, MNAME, CNAME, PID, EDIT_NAME FROM ( SELECT B.BID, B.BNAME, B.BOPEN_STATUS, B.BINTRO, M.MID, M.MNAME, C.CNAME, P.PID, P.EDIT_NAME, P.FTYPE, P.FILE_SRC, ROWNUM FROM BAND B JOIN MEMBER_BAND MB ON(B.BID = MB.BID) JOIN MEMBER M ON(MB.MID = M.MID) JOIN CATEGORY C ON(B.CID = C.CID) JOIN PROFILE P ON(B.BID = P.BID) WHERE B.BOPEN_STATUS != 'PRV' AND B.BSTATUS = 'Y' ORDER BY B.BID DESC )
OR UPPER(BNAME) LIKE '%'|| UPPER(#{item}) ||'%' OR UPPER(BINTRO) LIKE '%'|| UPPER(#{item}) ||'%'
) WHERE RNUM <= #{endPage} AND RNUM >= #{startPage}
